// export default TransitInfo;

import React from 'react';

const TransitInformation = () => {
  const routes = [
    { number: '1', name: 'Main Street' },
    { number: '2', name: 'Second Avenue' },
    { number: '3', name: 'Third Boulevard' },
  ];

  const schedule = [
    { time: '8:00 AM', destination: 'Downtown' },
    { time: '8:30 AM', destination: 'Uptown' },
    { time: '9:00 AM', destination: 'Airport' },
  ];

  const stops = [
    { name: 'Main St & 1st Ave', distance: '0.2 miles' },
    { name: 'Main St & 2nd Ave', distance: '0.5 miles' },
    { name: 'Main St & 3rd Blvd', distance: '0.7 miles' },
  ];

  const alerts = [
    { message: 'Route 1 is delayed due to construction.' },
    { message: 'Stop at Main St & 2nd Ave is temporarily closed.' },
  ];

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-2xl font-bold mb-4">Transit Information</h1>

      {/* Route Information */}
      <section className="mb-6">
        <h2 className="text-xl font-semibold">Routes</h2>
        <ul className="list-disc pl-5">
          {routes.map((route) => (
            <li key={route.number} className="mt-1">
              {route.number}: {route.name}
            </li>
          ))}
        </ul>
      </section>

      {/* Schedule Information */}
      <section className="mb-6">
        <h2 className="text-xl font-semibold">Schedule</h2>
        <ul className="list-disc pl-5">
          {schedule.map((item, index) => (
            <li key={index} className="mt-1">
              {item.time} - {item.destination}
            </li>
          ))}
        </ul>
      </section>

      {/* Stop Information */}
      <section className="mb-6">
        <h2 className="text-xl font-semibold">Nearby Stops</h2>
        <ul className="list-disc pl-5">
          {stops.map((stop, index) => (
            <li key={index} className="mt-1">
              {stop.name} - {stop.distance}
            </li>
          ))}
        </ul>
      </section>

      {/* Service Alerts */}
      <section>
        <h2 className="text-xl font-semibold">Service Alerts</h2>
        <ul className="list-disc pl-5">
          {alerts.map((alert, index) => (
            <li key={index} className="mt-1">
              {alert.message}
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
};

export default TransitInformation;
