import React, { useState, useEffect } from 'react';
import { APIProvider, Map, } from '@vis.gl/react-google-maps';

const MainMap = () => {
  const [userLocation, setUserLocation] = useState(null);

  useEffect(() => {
    const fetchUserLocation = () => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            setUserLocation({
              lat: position.coords.latitude,
              lng: position.coords.longitude,
            });
          },
          (error) => {
            console.error("Error getting user location:", error);
            // Set a default location if geolocation fails
            setUserLocation({ lat: 51.0447, lng: -114.0719 }); 
          }
        );
      } //else {
        //console.error("Geolocation is not supported by this browser.");
        // Set a default location if geolocation is not supported
        //setUserLocation({ lat: -33.860664, lng: 151.208138 }); 
      //}
    };
  return (
    <div className="flex flex-col md:flex-row h-auto md:h-96">
        <div className='w-full md:w-1/3 p-4'>
            <div className='flex items-center'>
                <img src='/images/bustop_blue.png' style={{ width: '30px' }} alt="Bus Stop Icon" />
                <h2 className="text-black text-2xl md:text-3xl text-center font-bold ml-2">STOPify</h2>
            </div>
    
            <div>
                <input
                    type="text"
                    placeholder="Search your bus..."
                    className="border border-gray-300 rounded-full p-2 m-4 w-full md:w-80" 
                />
            </div>
    
            <div>
                <Card />
            </div>
        </div>

        <div className='w-full md:w-2/3'>
        <APIProvider apiKey={'AIzaSyA4u5WHz6-4ldEWPwyrjjjhhtkOwVm1lyo'} onLoad={() => console.log('Maps API has loaded.')}>
            <Map
              className="rounded-lg overflow-hidden w-full h-80 md:h-full"
                defaultZoom={13}
                defaultCenter={ { userLocation } }
                onCameraChanged={ (ev: MapCameraChangedEvent) =>
                  console.log('camera changed:', ev.detail.center, 'zoom:', ev.detail.zoom)
                }>
            </Map>
        </APIProvider>
        </div>     
    </div>
);


    fetchUserLocation();
  }, []);

  return (
    <div className="flex justify-center items-center h-96 bg-gray-200">
      <APIProvider apiKey={'AIzaSyA4u5WHz6-4ldEWPwyrjjjhhtkOwVm1lyo'} onLoad={() => console.log('Maps API has loaded.')}>
        {userLocation && ( // Conditionally render the map when userLocation is available
          <Map
            defaultZoom={16}
            defaultCenter={userLocation} // Use userLocation for defaultCenter
          >
          </Map>
        )}
      </APIProvider>
    </div>
  );
};

export default MainMap;
